import UIKit

protocol FavoriteDetailBusinessLogic: AnyObject {
  func fetchFavorites()
}

protocol FavoriteDetailDataStore: AnyObject {
    var favoriteItem: FavoriteItem? { get set }
}

class FavoriteDetailInteractor: FavoriteDetailBusinessLogic, FavoriteDetailDataStore {
    
    var favoriteItem: FavoriteItem?
    var presenter: FavoriteDetailPresentationLogic?
    var worker: FavoriteDetailWorker?
  
    init(worker: FavoriteDetailWorker){
        self.worker = worker
    }
    
    func fetchFavorites() {
        guard let favoriteItem = self.favoriteItem else {
            return
        }
        self.presenter?.presentSomething(response: FavoriteDetail.Fetch.Response(favItem: favoriteItem))
    }
}
