import UIKit

protocol FavoriteDetailRoutingLogic {
    func routeToWebKit()
}

protocol FavoriteDetailDataPassing {
  var dataStore: FavoriteDetailDataStore? { get }
}

final class FavoriteDetailRouter: FavoriteDetailRoutingLogic, FavoriteDetailDataPassing {

  weak var viewController: FavoriteDetailViewController?
  var dataStore: FavoriteDetailDataStore?
    
    func routeToWebKit() {
        guard let url = URL(string: dataStore?.favoriteItem?.webUrl ?? "") else {
            return
        }
        let vc = WebViewController()
        vc.webView.load(URLRequest(url: url))
        viewController?.navigationController?.pushViewController(vc, animated: true)
    }
}
