import UIKit

protocol NewsDetailBusinessLogic: AnyObject {
  func fetchNews()
}

protocol NewsDetailDataStore: AnyObject {
    var articles: Article? { get set }
    var favorites: FavoriteNews.Fetch.ViewModel.Favorites? { get set }
}

class NewsDetailInteractor: NewsDetailBusinessLogic, NewsDetailDataStore {
    var favorites: FavoriteNews.Fetch.ViewModel.Favorites?

    var articles: Article?
    var presenter: NewsDetailPresentationLogic?
    var worker: NewsDetailWorker?
    
    init(worker: NewsDetailWorker){
        self.worker = worker
    }
  
  func fetchNews() {
      guard let articles = self.articles else {
          return
      }
      self.presenter?.presentArticle(response: NewsDetail.Fetch.Response(articles: articles))
  }
}
