import UIKit
import Alamofire

protocol NewsListWorkingLogic: AnyObject {
    func getNewsList(completion: @escaping ((Result<NewsListResponse, Error>) -> Void))
}

final class NewsListWorker: NewsListWorkingLogic {
    func getNewsList(completion: @escaping ((Result<NewsListResponse, Error>) -> Void)) {
        Requester().request(model: NewsListResponse.self, parameters: [:]) { result in
            switch result {
            case .httpSuccess(let response):
                completion(.success(response))
            case .httpFail(let error):
                completion(.failure(error))
            }
        }
    }
}
